import React, { useEffect, useRef,useState } from 'react';
import Header from './Header';
import Crud from './Crud';
import { Toast } from 'primereact/toast';
import { Dialog } from 'primereact/dialog';
import { ProgressSpinner } from 'primereact/progressspinner';

export default function Categorie() {
  const toast = useRef(null);
  const [visible, setVisible] = useState(false);
  const [categorie, setCategorie] = useState([]);
  const [newCategorie, setNewCatgorie] = useState('');

  useEffect(() =>{
    const fetchData = async () => {
      try {
        const reponse = await fetch('http://localhost:8081/api/marque/all');

        if(!reponse.ok){
          throw new Error ('erreur lors de la recuperation des details');
        }

        const data = await reponse.json();
        setCategorie(data);
      } catch (error) {
        console.error('Erreur :', error.message);
      }
    };
    fetchData();
  }, []);

  const showSuccess = () => {
      toast.current.show({severity:'success', summary: 'Success', detail:'Ajouter avec succes', life: 3000});
  }

  const showError = () => {
    toast.current.show({severity:'error', summary: 'Error', detail:'Element Supprimer', life: 3000});
}

const AddMarque = async () => {
  try {
    const reponse = await fetch('http://localhost:8081/api/marque/create', {
      method: 'POST',
      headers:{
        'Content-type': 'application/json',
      },
      body: JSON.stringify({marque : newCategorie}),
    }).then((item) => {
      window.location.reload();
    });

    if(!reponse.ok){
      throw new Error('Erreur lors de la creation de la categorie');
    }

    const data = await reponse.json();
    console.log(data);

  } catch (error) {
    console.error('Erreur:', error.message);
  }
};

const DeleteMarque = async (idMarque) => {
  try {
    const reponse = await fetch(`http://localhost:8081/api/marque/delete/${idMarque}`, {
      method: 'DELETE',
      headers:{
        'Content-type': 'application/json',
      },
    }).then((item)=>{
      window.location.reload();
    })

    if(!reponse.ok){
      throw new Error('Erreur lors de la suppression');
    }

    const data = await reponse.json();
    console.log(data);

  } catch (error) {
    console.error(error.message);
  }
};

const UpdateMarque = async (idMarque) => {
  try {
    const reponse = await fetch(`http://localhost:8081/api/marque/update/${idMarque}`, {
      method: 'PUT',
      headers: {
        'Content-type': 'application/json',
      },
      body: JSON.stringify({})
    })
  } catch (error) {
    console.error(error.message);
  }
}

if (!categorie) {
  return (
    <div className="d-flex align-items-center justify-content-center vh-100">
    <div>
      <ProgressSpinner style={{ width: '50px', height: '100px' }} strokeWidth="8" fill="var(--surface-ground)" animationDuration=".5s" />
    </div>
  </div>
);
}

  return (
    <>
    <Header/>
    <Toast ref={toast} position="top-center" />

    <Dialog header="Header" visible={visible} style={{ width: '40vw' }} onHide={() => setVisible(false)}>
      <div className="m-0">
        <h1 className='text-center' >Modification d'un elements</h1>
        <form class="">
          <div class="form-floating mb-3">
            <input type="hidden" name="idCategorie" />
            <input type="text" class="form-control rounded-3" id="floatingInput"/>
            <label for="floatingInput">Marque:</label>
          </div>
          <div className='text-center' >
          <button class="w-50 mb-2 btn btn-lg rounded-3 btn-warning" type="submit"><i className='fas fa-pen' ></i>Modifier</button>
          </div>
        </form>
      </div>
    </Dialog>

    <div className="container-fluid">
      <div className="row">
        <div className="col-md-9">
          <div className='container'>
          <h1 className="text-start">Gestion des Marques:</h1>
          <div className="row">

            <form className="mb-3">
              <div className="input-group">
                <input type="text" className="form-control" value={newCategorie} onChange={(e) => setNewCatgorie(e.target.value)} name='AddCategorie' placeholder="Nouvelle CatÃ©gorie" />
                <button type="button" className="btn btn-primary" onClick={AddMarque} ><i className='fas fa-plus' ></i> Ajouter</button>
              </div>
            </form>

            <table className='table table-sm table-borderless text-center' >
              <thead className='table-dark' >
                <tr>
                  <th>code marque</th>
                  <th>marque</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody className='table-striped' >

                {categorie.map((cat) =>(
                <tr>
                  <th>{cat.idMarque}</th>
                  <th>{cat.marque}</th>
                  <th><button className='btn btn-warning w-25' onClick={() => setVisible(true)} ><i className='fas fa-pen' ></i> Modifier</button>  <button className='btn btn-danger w-25' onClick={() => DeleteMarque(cat.idMarque)} ><i className='fas fa-trash-alt' ></i> Supprimer</button></th>
                </tr>
                ))}

              </tbody>
            </table>

          </div>
          </div>
        </div>

        <Crud/>
      </div>
    </div>
    </>
  );
}
